/**
 * Supabase Client for React Native
 *
 * Environment variables are loaded from app.config.js extra.eas.env
 * or from .env file during development
 */

import { createClient } from '@supabase/supabase-js'

// Declare process.env for Expo environment variables
declare global {
  // eslint-disable-next-line @typescript-eslint/no-namespace
  namespace NodeJS {
    interface ProcessEnv {
      EXPO_PUBLIC_SUPABASE_URL?: string;
      EXPO_PUBLIC_SUPABASE_ANON_KEY?: string;
      NEXT_PUBLIC_SUPABASE_URL?: string;
      NEXT_PUBLIC_SUPABASE_ANON_KEY?: string;
    }
  }
}

// These will be replaced by app.config.js during build
// For local development, you can also use .env file
const supabaseUrl = process?.env?.EXPO_PUBLIC_SUPABASE_URL || process?.env?.NEXT_PUBLIC_SUPABASE_URL || ''
const supabaseAnonKey = process?.env?.EXPO_PUBLIC_SUPABASE_ANON_KEY || process?.env?.NEXT_PUBLIC_SUPABASE_ANON_KEY || ''

if (!supabaseUrl || !supabaseAnonKey) {
  console.warn('Supabase URL or Anon Key not configured. Please set EXPO_PUBLIC_SUPABASE_URL and EXPO_PUBLIC_SUPABASE_ANON_KEY in app.config.js or .env file.')
}

export const supabase = createClient(supabaseUrl, supabaseAnonKey, {
  auth: {
    storage: (globalThis as any).localStorage, // Use AsyncStorage if needed
    autoRefreshToken: true,
    persistSession: true,
    detectSessionInUrl: false,
  },
})

/**
 * Type declarations for className support in React Native
 * NOTE: This provides TypeScript support for className props.
 * For runtime functionality, NativeWind v4 must be properly installed.
 *
 * To install NativeWind v4:
 * npm install nativewind tailwindcss
 * npm install -D @types/babel__core
 */

import type { ViewStyle, TextStyle, ImageStyle } from 'react-native';

declare module 'react-native' {
  interface ViewProps {
    className?: string;
    style?: ViewStyle;
  }
  interface TextProps {
    className?: string;
    style?: TextStyle;
  }
  interface ImageProps {
    className?: string;
    style?: ImageStyle;
  }
  interface ScrollViewProps {
    className?: string;
    contentContainerClassName?: string;
    contentContainerStyle?: ViewStyle;
  }
  interface FlatListProps<T> {
    className?: string;
    contentContainerClassName?: string;
    contentContainerStyle?: ViewStyle;
  }
}

/**
 * Button Component
 * Styled button following design guidelines
 * Uses React Native Paper with custom styling
 */

import React from 'react';
import { Button as PaperButton } from 'react-native-paper';
import { StyleSheet, ViewStyle } from 'react-native';

export interface ButtonProps {
  children: React.ReactNode | string | number | boolean | null | undefined;
  mode?: 'contained' | 'outlined' | 'text' | 'elevated' | 'contained-tonal';
  onPress?: () => void;
  loading?: boolean;
  disabled?: boolean;
  icon?: string;
  style?: ViewStyle;
  textColor?: string;
  buttonColor?: string;
  uppercase?: boolean;
}

const Button: React.FC<ButtonProps> = ({
  mode = 'contained',
  children,
  style,
  buttonColor,
  textColor,
  uppercase = false,
  ...props
}) => {
  return (
    <PaperButton
      mode={mode}
      style={[styles.button, style]}
      contentStyle={styles.content}
      labelStyle={styles.label}
      buttonColor={buttonColor}
      textColor={textColor}
      uppercase={uppercase}
      {...props}
    >
      {children as any}
    </PaperButton>
  );
};

const styles = StyleSheet.create({
  button: {
    borderRadius: 12,
    marginVertical: 8,
  },
  content: {
    paddingVertical: 8,
    paddingHorizontal: 24,
    minHeight: 48,
  },
  label: {
    fontSize: 14,
    fontWeight: '700',
    letterSpacing: 0.5,
  },
});

export default Button;

/**
 * Card Component
 * Styled card following design guidelines
 * Uses React Native Paper with custom styling
 */

import React from 'react';
import { Card as PaperCard } from 'react-native-paper';
import { StyleSheet, ViewStyle } from 'react-native';

export interface CardProps {
  children: React.ReactNode | string | number | boolean | null | undefined;
  style?: ViewStyle;
  variant?: 'elevated' | 'outlined' | 'contained';
  onPress?: () => void;
}

const Card: React.FC<CardProps> = ({
  children,
  style,
  variant = 'elevated',
  onPress,
}) => {
  // Map variant to Paper mode ('contained' maps to 'elevated')
  const mode = variant === 'outlined' ? 'outlined' : 'elevated';

  return (
    <PaperCard
      mode={mode}
      style={[styles.card, style]}
      contentStyle={styles.content}
      onPress={onPress}
    >
      {children as any}
    </PaperCard>
  );
};

const styles = StyleSheet.create({
  card: {
    borderRadius: 16,
    marginVertical: 8,
    marginHorizontal: 16,
    overflow: 'hidden',
  },
  content: {
    padding: 16,
  },
});

export default Card;

/**
 * Simple logger for Expo Go debugging
 * Logs are stored in memory and can be viewed in the DebugLogs screen
 */

export type LogLevel = 'info' | 'warn' | 'error' | 'debug';

export interface LogEntry {
  timestamp: string;
  level: LogLevel;
  tag: string;
  message: string;
  data?: any;
}

class Logger {
  private logs: LogEntry[] = [];
  private maxLogs = 500;

  log(level: LogLevel, tag: string, message: string, data?: any) {
    const entry: LogEntry = {
      timestamp: new Date().toISOString(),
      level,
      tag,
      message,
      data,
    };

    this.logs.push(entry);

    // Keep only the last maxLogs entries
    if (this.logs.length > this.maxLogs) {
      this.logs.shift();
    }

    // Also log to console
    const consoleMethod = level === 'error' ? console.error : level === 'warn' ? console.warn : console.log;
    consoleMethod(`[${level.toUpperCase()}][${tag}]`, message, data || '');
  }

  info(tag: string, message: string, data?: any) {
    this.log('info', tag, message, data);
  }

  warn(tag: string, message: string, data?: any) {
    this.log('warn', tag, message, data);
  }

  error(tag: string, message: string, data?: any) {
    this.log('error', tag, message, data);
  }

  debug(tag: string, message: string, data?: any) {
    this.log('debug', tag, message, data);
  }

  getLogs(): LogEntry[] {
    return [...this.logs];
  }

  clearLogs() {
    this.logs = [];
  }

  getLogsAsString(): string {
    return this.logs
      .map((log) => `[${log.timestamp}] [${log.level.toUpperCase()}] [${log.tag}] ${log.message}${log.data ? ' ' + JSON.stringify(log.data) : ''}`)
      .join('\n');
  }
}

export const logger = new Logger();

// Convenience functions
export const logInfo = (tag: string, message: string, data?: any) => logger.info(tag, message, data);
export const logWarn = (tag: string, message: string, data?: any) => logger.warn(tag, message, data);
export const logError = (tag: string, message: string, data?: any) => logger.error(tag, message, data);
export const logDebug = (tag: string, message: string, data?: any) => logger.debug(tag, message, data);

/**
 * Supabase client for React Native
 * Uses @supabase/supabase-js with AsyncStorage for persisting sessions
 */

import { createClient } from '@supabase/supabase-js';
import AsyncStorage from '@react-native-async-storage/async-storage';

// Declare process.env for Expo environment variables
declare global {
  // eslint-disable-next-line @typescript-eslint/no-namespace
  namespace NodeJS {
    interface ProcessEnv {
      EXPO_PUBLIC_SUPABASE_URL?: string;
      EXPO_PUBLIC_SUPABASE_ANON_KEY?: string;
    }
  }

  const process: {
    env: NodeJS.ProcessEnv;
  };
}

// Get environment variables from app.config.js or .env
const supabaseUrl = (process?.env?.EXPO_PUBLIC_SUPABASE_URL as string) || '';
const supabaseAnonKey = (process?.env?.EXPO_PUBLIC_SUPABASE_ANON_KEY as string) || '';

// Debug logging
const log = (tag: string, ...args: unknown[]) => {
  console.log(`[SUPABASE:${tag}]`, ...args);
};

log('CONFIG', 'Initializing Supabase client...');
log('CONFIG', `URL: ${supabaseUrl}`);
log('CONFIG', `Anon Key: ${supabaseAnonKey ? supabaseAnonKey.substring(0, 20) + '...' : 'MISSING'}`);

if (!supabaseUrl || !supabaseAnonKey) {
  console.error('[SUPABASE] URL or Anon Key is missing!');
  console.error('[SUPABASE] Please set EXPO_PUBLIC_SUPABASE_URL and EXPO_PUBLIC_SUPABASE_ANON_KEY in app.json');
}

// Create a custom storage adapter for React Native
const customStorageAdapter = {
  getItem: (key: string) => {
    return AsyncStorage.getItem(key);
  },
  setItem: (key: string, value: string) => {
    return AsyncStorage.setItem(key, value);
  },
  removeItem: (key: string) => {
    return AsyncStorage.removeItem(key);
  },
};

// Create Supabase client without Database type (not exported from shared-types)
// TODO: Add Database type to shared-types when available
export const supabase = createClient(
  supabaseUrl,
  supabaseAnonKey,
  {
    auth: {
      storage: customStorageAdapter as any,
      autoRefreshToken: true,
      persistSession: true,
      detectSessionInUrl: false,
      debug: true, // Enable Supabase auth debugging
    },
    global: {
      headers: {
        'X-Client-Info': 'econtact-mobile',
      },
    },
  }
);

log('INIT', 'Supabase client initialized successfully');

export default supabase;

/**
 * Supabase Queries
 * Reusable database queries for the mobile app
 */

import { supabase } from './client';

export interface ChildData {
  id: string;
  name: string;
  rollNumber: string;
  classId: string;
  section: string;
  grade: number;
  studentCode: string;
  isPrimary?: boolean;
  avatarUrl?: string;
}

/**
 * Fetch all children for a given parent from student_guardians junction table
 * @param parentId - UUID from parents table
 * @returns Array of child data
 */
export const getParentChildren = async (
  parentId: string
): Promise<ChildData[]> => {
  const { data, error } = await supabase
    .from('student_guardians')
    .select(`
      student_id,
      is_primary,
      students!inner(
        id,
        student_code,
        profiles!students_id_fkey(
          full_name,
          avatar_url,
          status
        ),
        enrollments!inner(
          class_id,
          status,
          classes!inner(
            name,
            grade_id
          )
        )
      )
    `)
    .eq('guardian_id', parentId)
    .eq('students.profiles!students_id_fkey.status', 'active')
    .eq('students.enrollments.status', 'active');

  if (error) {
    console.error('[QUERIES] Error fetching children:', error);
    throw new Error(`Failed to load children: ${error.message}`);
  }

  if (!data || data.length === 0) {
    return [];
  }

  // Group by student (in case multiple enrollments exist, get the active one)
  const uniqueChildren = new Map<string, any>();

  for (const item of data as any[]) {
    const studentId = item.students.id;
    if (!uniqueChildren.has(studentId)) {
      uniqueChildren.set(studentId, item);
    }
  }

  return Array.from(uniqueChildren.values()).map((item: any) => ({
    id: item.students.id,
    name: item.students.full_name,
    rollNumber: item.students.student_code,
    classId: item.students.enrollments?.[0]?.class_id || '',
    section: '',
    grade: parseInt(item.students.enrollments?.[0]?.classes?.grade_id || '0', 10),
    studentCode: item.students.student_code,
    isPrimary: item.is_primary,
    avatarUrl: item.students.avatar_url,
  }));
};

/**
 * Get parent ID from profile phone number
 * @param phone - Parent phone number
 * @returns Parent UUID or null
 */
export const getParentIdByPhone = async (
  phone: string
): Promise<string | null> => {
  // First get profile ID from phone, then get parent record
  const { data: profile, error: profileError } = await supabase
    .from('profiles')
    .select('id')
    .eq('phone', phone)
    .eq('role', 'parent')
    .single();

  if (profileError || !profile) {
    console.error('[QUERIES] Error finding parent profile:', profileError);
    return null;
  }

  // Profile ID is same as parent ID in our schema
  return profile.id;
};

/**
 * Auth Navigator
 * Handles authentication screens
 */

import React from 'react';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { useAuthStore } from '../stores';

import CustomLoginScreen from '../screens/auth/CustomLoginScreen';
import DebugLogsScreen from '../screens/debug/DebugLogsScreen';

export type AuthStackParamList = {
  Login: undefined;
  DebugLogs: undefined;
};

const Stack = createNativeStackNavigator<AuthStackParamList>();

const AuthNavigator: React.FC = () => {
  return (
    <Stack.Navigator
      screenOptions={{
        headerShown: false,
        contentStyle: { backgroundColor: '#F8FAFC' },
      }}
    >
      <Stack.Screen name="Login" component={CustomLoginScreen as any} />
      <Stack.Screen
        name="DebugLogs"
        component={DebugLogsScreen as any}
        options={{
          headerShown: true,
          title: 'Debug Logs',
          headerStyle: { backgroundColor: '#0F172A' },
          headerTintColor: '#FFFFFF',
        }}
      />
    </Stack.Navigator>
