 */

import React, { useState } from "react";
import { View, Text, ScrollView, TouchableOpacity } from "react-native";
import { useParentStore } from "../../stores";

interface Period {
  id: string;
  periodNumber: number;
  time: string;
  subject: string;
  subjectShort: string;
  teacher: string;
  room: string;
  session: "morning" | "afternoon";
  color: string;
}

interface DaySchedule {
  date: string;
  dayLabel: string;
  dayName: string;
  dayNumber: number;
  periods: Period[];
}

const MOCK_SCHEDULE: DaySchedule[] = [
  {
    date: "2026-01-06",
    dayLabel: "T2",
