        teacher: "Nguyễn Thị Lan",
        room: "Phòng 301",
        session: "morning",
        color: "bg-blue-100 text-blue-600",
      },
      {
        id: "2",
        periodNumber: 2,
        time: "07:50 - 08:35",
        subject: "Ngữ văn",
