        teacher: "Phạm Quốc Khánh",
        room: "Phòng Lab 1",
        session: "afternoon",
        color: "bg-indigo-100 text-indigo-600",
      },
      {
        id: "7",
        periodNumber: 7,
        time: "14:20 - 15:05",
        subject: "Hóa học",
        subjectShort: "Hóa",
