 * Contact list of teachers
 */

import React from 'react';
import { View, FlatList, TouchableOpacity, ScrollView, StyleSheet, Text } from 'react-native';
import { mockTeachers } from '../../mock-data';

interface Teacher {
  id: string;
  name: string;
