      gap: 4,
    },
    contactRow: {
      flexDirection: 'row',
      alignItems: 'center',
    },
    contactLabel: {
      fontSize: 12,
      color: '#9ca3af',
      fontWeight: '500',
      marginRight: 8,
      width: 45,
    },
    contactValue: {
      fontSize: 12,
      color: '#6b7280',
