/**
 * Teacher Feedback Screen
 * Comments and feedback from teachers
 */

import React, { useState } from "react";
import { View, ScrollView, Text, TouchableOpacity } from "react-native";
import Svg, { Path, Circle, Line, Polygon } from "react-native-svg";
import { useParentStore } from "../../stores";
import { colors } from "../../theme";

interface Feedback {
  id: string;
  teacherName: string;
  teacherAvatar: string;
  subject: string;
  message: string;
  date: string;
  sentiment: "positive" | "neutral" | "concern";
  rating: number;
}

const MOCK_FEEDBACK: Feedback[] = [
  {
    id: "1",
    teacherName: "Nguyễn Thị Lan",
    teacherAvatar: "GV",
    subject: "Giáo viên Toán học",
    message: "Em có tiến bộ rất tốt trong giải các bài toán khó. Cần luyện thêm về dạng bài hình học không gian.",
    date: "05/01/2026",
    sentiment: "positive",
    rating: 5,
  },
  {
    id: "2",
    teacherName: "Lê Thu Hương",
    teacherAvatar: "LH",
    subject: "Giáo viên Tiếng Anh",
    message: "Nói tiếng Anh rất tự nhiên. Phát âm tốt nhưng cần mở rộng từ vựng hơn nữa.",
    date: "03/01/2026",
    sentiment: "positive",
    rating: 4,
  },
  {
    id: "3",
    teacherName: "Phạm Quốc Khánh",
    teacherAvatar: "PQK",
    subject: "Giáo viên Vật lý",
    message: "Cần chú ý hơn khi làm bài tập về nhà. Vắng 2 buổi học thực hành cần bù lại.",
    date: "28/12/2025",
    sentiment: "concern",
    rating: 2,
  },
];

type FilterType = "all" | "positive" | "concern";

const SENTIMENT_CONFIG = {
  positive: {
    label: "Tích cực",
    color: "#9333EA",
    bgColor: "#F3E8FF",
    badgeColor: "#F3E8FF",
    badgeText: "#9333EA",
  },
  concern: {
    label: "Cần lưu ý",
    color: "#D97706",
    bgColor: "#FEF3C7",
    badgeColor: "#FEF3C7",
    badgeText: "#D97706",
  },
  neutral: {
    label: "Cần cải thiện",
    color: "#D97706",
    bgColor: "#FEF3C7",
    badgeColor: "#FEF3C7",
    badgeText: "#D97706",
  },
};

const StarIcon = ({ filled }: { filled: boolean }) => {
  const fill = filled ? '#F59E0B' : '#D1D5DB';
  return (
    <Svg width={14} height={14} viewBox='0 0 24 24'>
      <Polygon
        points='12 2 15.09 8.26 22 9.27 17 14.14 18.18 21.02 12 17.77 5.82 21.02 7 14.14 2 9.27 8.91 8.26 12 2'
        fill={fill}
      />
    </Svg>
  );
};

export const TeacherFeedbackScreen: React.FC = () => {
  const { children, selectedChildId } = useParentStore();
  const selectedChild = children.find((c) => c.id === selectedChildId) || children[0];
  const [activeFilter, setActiveFilter] = useState<FilterType>("all");

  const filteredFeedback = MOCK_FEEDBACK.filter((item) => {
    if (activeFilter === "all") return true;
    if (activeFilter === "positive") return item.sentiment === "positive";
    if (activeFilter === "concern") return item.sentiment === "concern";
    return true;
  });

  const positiveCount = MOCK_FEEDBACK.filter((f) => f.sentiment === "positive").length;
  const concernCount = MOCK_FEEDBACK.filter((f) => f.sentiment === "concern").length;

  const FilterTab = ({ label, filter }: { label: string; filter: FilterType }) => {
    const isActive = activeFilter === filter;
    return (
      <TouchableOpacity
        onPress={() => setActiveFilter(filter)}
        className={"flex-shrink-0 px-4 py-2 rounded-xl " + (isActive ? "bg-[#0284C7]" : "bg-white border border-gray-200")}
      >
        <Text
          className={"text-xs font-black uppercase " + (isActive ? "text-white" : "text-gray-400")}
        >
          {label}
        </Text>
      </TouchableOpacity>
    );
  };

  const FeedbackCard = ({ item }: { item: Feedback }) => {
    const config = SENTIMENT_CONFIG[item.sentiment];
    const isConcern = item.sentiment === "concern";
    return (
      <View
        className={"bg-white p-4 rounded-2xl " + (isConcern ? "border border-amber-200" : "border border-gray-100") + " shadow-sm mb-3"}
      >
        <View className="flex-row justify-between items-start mb-3">
          <View className="flex-row items-center space-x-2">
            <View className="px-2 py-0.5 rounded-full" style={{ backgroundColor: config.badgeColor }}>
              <Text className="text-[8px] font-black uppercase" style={{ color: config.badgeText }}>
                {config.label}
              </Text>
            </View>
            <Text className="text-gray-400 text-[9px] font-medium">{item.date}</Text>
          </View>
          <View className="flex-row items-center space-x-1">
            {[1, 2, 3, 4, 5].map((star) => (
              <StarIcon key={star} filled={star <= item.rating} />
            ))}
          </View>
        </View>
        <View className="flex-row items-start space-x-3">
          <View
            className="w-10 h-10 rounded-xl justify-center items-center"
            style={{
              backgroundColor:
                item.teacherAvatar === "GV"
                  ? "#DBEAFE"
                  : item.teacherAvatar === "LH"
                  ? "#D1FAE5"
                  : "#E0E7FF",
            }}
          >
            <Text
              className="font-black text-sm"
              style={{
                color:
                  item.teacherAvatar === "GV"
                    ? "#0284C7"
                    : item.teacherAvatar === "LH"
                    ? "#059669"
                    : "#4F46E5",
              }}
            >
              {item.teacherAvatar}
            </Text>
          </View>
          <View className="flex-1">
            <Text className="text-gray-800 font-bold text-sm mb-0.5">
              {item.teacherName}
            </Text>
            <Text className="text-gray-400 text-[9px] font-medium mb-2">
              {item.subject}
            </Text>
            <Text className="text-gray-600 text-xs leading-snug">
              {item.message}
            </Text>
          </View>
        </View>
      </View>
    );
  };

  const ThumbsUpIcon = () => (
    <Svg width={16} height={16} viewBox='0 0 24 24' fill='none' stroke='white' strokeWidth={3}>
      <Path d='M14 9V5a3 3 0 0 0-3-3l-4 9v11h11.28a2 2 0 0 0 2-1.7l1.38-9a2 2 0 0 0-2-2.3zM7 22H4a2 2 0 0 1-2-2v-7a2 2 0 0 1 2-2h3' />
    </Svg>
  );

  const AlertIcon = () => (
    <Svg width={16} height={16} viewBox='0 0 24 24' fill='none' stroke='white' strokeWidth={3}>
      <Circle cx='12' cy='12' r='10' />
      <Line x1='12' y1='8' x2='12' y2='12' />
      <Line x1='12' y1='16' x2='12.01' y2='16' />
    </Svg>
  );

  const BackIcon = () => (
    <Svg width={20} height={20} viewBox='0 0 24 24' fill='none' stroke='white' strokeWidth={2.5}>
      <Path d='M19 12H5' />
      <Path d='M12 19l-7-7 7-7' />
    </Svg>
  );

  return (
    <View className="flex-1 bg-[#F8FAFC]">
      <View
        className="absolute top-0 left-0 right-0 h-35 z-0"
        style={{
          backgroundColor: "#0284C7",
          borderBottomLeftRadius: 30,
          borderBottomRightRadius: 30,
        }}
      />
      <View className="relative z-10 px-6 pt-16 pb-4">
        <View className="flex-row items-center space-x-4">
          <TouchableOpacity
            className="w-10 h-10 bg-white/20 rounded-full justify-center items-center"
            onPress={() => {}}
          >
            <BackIcon />
          </TouchableOpacity>
          <View>
            <Text className="text-white text-xl font-extrabold">Nhận xét giáo viên</Text>
            <Text className="text-blue-100 text-xs font-medium mt-0.5">
              Nhận xét và đánh giá của giáo viên
            </Text>
          </View>
        </View>
      </View>
      <ScrollView
        className="flex-1 px-6 pt-6"
        contentContainerStyle={{ paddingBottom: 128 }}
        showsVerticalScrollIndicator={false}
      >
        <View className="flex-row space-x-2 mb-5 overflow-x-auto">
          <FilterTab label="Tất cả" filter="all" />
          <FilterTab label="Tích cực" filter="positive" />
          <FilterTab label="Cần cải thiện" filter="concern" />
        </View>
        <View className="flex-row space-x-3 mb-6">
          <View
            className="flex-1 p-4 rounded-2xl"
            style={{
              backgroundColor: "#A855F7",
              shadowColor: "#6B21A8",
              shadowOffset: { width: 0, height: 4 },
              shadowOpacity: 0.2,
              shadowRadius: 8,
              elevation: 4,
            }}
          >
            <View className="flex-row items-center space-x-2 mb-1">
              <ThumbsUpIcon />
              <Text className="text-purple-100 text-[9px] font-black uppercase">
                Tích cực
              </Text>
            </View>
            <Text className="text-white text-3xl font-extrabold">{positiveCount}</Text>
          </View>
          <View
            className="flex-1 p-4 rounded-2xl"
            style={{
              backgroundColor: "#F59E0B",
              shadowColor: "#B45309",
              shadowOffset: { width: 0, height: 4 },
              shadowOpacity: 0.2,
              shadowRadius: 8,
              elevation: 4,
            }}
          >
            <View className="flex-row items-center space-x-2 mb-1">
              <AlertIcon />
              <Text className="text-amber-100 text-[9px] font-black uppercase">
                Cần lưu ý
              </Text>
            </View>
            <Text className="text-white text-3xl font-extrabold">{concernCount}</Text>
          </View>
        </View>
        <Text className="text-gray-800 font-extrabold text-sm mb-3">
          Nhận xét gần đây
        </Text>
        {filteredFeedback.map((item) => (
          <FeedbackCard key={item.id} item={item} />
        ))}
      </ScrollView>
    </View>
  );
};

/**
 * Biometric Authentication Screen
 * Allows users to enable/disable biometric authentication
 */

import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  Switch,
  Alert,
  Platform,
  StyleSheet,
} from 'react-native';
import { useUIStore } from '../../stores';
import { colors } from '../../theme';
import AsyncStorage from '@react-native-async-storage/async-storage';
import Svg, { Path, Rect, Circle } from 'react-native-svg';
import type { NativeStackNavigationProp } from '@react-navigation/native-stack';

interface BiometricAuthScreenProps {
  navigation?: NativeStackNavigationProp<any>;
}

const BackIcon = () => (
  <Svg width={24} height={24} viewBox="0 0 24 24" fill="none">
    <Path d="M19 12H5M12 19l-7-7 7-7" stroke={colors.primary} strokeWidth={2} strokeLinecap="round" strokeLinejoin="round"/>
  </Svg>
);

const FingerprintIcon = ({ color, size = 48 }: { color: string; size?: number }) => (
  <Svg width={size} height={size} viewBox="0 0 24 24" fill="none">
    <Path d="M2 12C2 6.5 6.5 2 12 2a10 10 0 0 1 8 6" stroke={color} strokeWidth={2} strokeLinecap="round" strokeLinejoin="round"/>
    <Path d="M5 19.5C5.5 18 6 15 6 12a6 6 0 0 1 6-6 6 6 0 0 1 6 6c0 1-1 2-2 2s-2-1-2-2a2 2 0 0 0-2-2 2 2 0 0 0-2 2c0 4 2 7 4 8" stroke={color} strokeWidth={2} strokeLinecap="round" strokeLinejoin="round"/>
    <Path d="M9 20c1-2 3-4 4-5s3-2 4-1" stroke={color} strokeWidth={2} strokeLinecap="round" strokeLinejoin="round"/>
  </Svg>
);

const FaceIdIcon = ({ size = 48 }: { size?: number }) => (
  <Svg width={size} height={size} viewBox="0 0 24 24" fill="none">
    <Path d="M3 7V5a2 2 0 0 1 2-2h2" stroke={colors.primary} strokeWidth={2} strokeLinecap="round" strokeLinejoin="round"/>
    <Path d="M17 3h2a2 2 0 0 1 2 2v2" stroke={colors.primary} strokeWidth={2} strokeLinecap="round" strokeLinejoin="round"/>
    <Path d="M21 17v2a2 2 0 0 1-2 2h-2" stroke={colors.primary} strokeWidth={2} strokeLinecap="round" strokeLinejoin="round"/>
    <Path d="M7 21H5a2 2 0 0 1-2-2v-2" stroke={colors.primary} strokeWidth={2} strokeLinecap="round" strokeLinejoin="round"/>
    <Circle cx="9" cy="9" r="2" stroke={colors.primary} strokeWidth={2} strokeLinecap="round" strokeLinejoin="round"/>
    <Circle cx="15" cy="9" r="2" stroke={colors.primary} strokeWidth={2} strokeLinecap="round" strokeLinejoin="round"/>
    <Path d="M9 17c.5-1 2-2 3-2s2.5 1 3 2" stroke={colors.primary} strokeWidth={2} strokeLinecap="round" strokeLinejoin="round"/>
  </Svg>
);

const LockIcon = () => (
  <Svg width={20} height={20} viewBox="0 0 24 24" fill="none">
    <Rect x="3" y="11" width="18" height="11" rx="2" ry="2" stroke="#9CA3AF" strokeWidth={2} strokeLinecap="round" strokeLinejoin="round"/>
    <Path d="M7 11V7a5 5 0 0 1 10 0v4" stroke="#9CA3AF" strokeWidth={2} strokeLinecap="round" strokeLinejoin="round"/>
  </Svg>
);

const UnlockIcon = () => (
  <Svg width={20} height={20} viewBox="0 0 24 24" fill="none">
    <Rect x="3" y="11" width="18" height="11" rx="2" ry="2" stroke={colors.primary} strokeWidth={2} strokeLinecap="round" strokeLinejoin="round"/>
    <Path d="M7 11V7a5 5 0 0 1 9.9-1" stroke={colors.primary} strokeWidth={2} strokeLinecap="round" strokeLinejoin="round"/>
  </Svg>
);

const CheckIcon = () => (
  <Svg width={20} height={20} viewBox="0 0 24 24" fill="none">
    <Path d="M20 6L9 17l-5-5" stroke="#4CAF50" strokeWidth={2.5} strokeLinecap="round" strokeLinejoin="round"/>
  </Svg>
);

const BIOMETRIC_STORAGE_KEY = 'biometric_enabled';

export const BiometricAuthScreen: React.FC<BiometricAuthScreenProps> = ({ navigation }) => {
  const { isBiometricEnabled, toggleBiometric } = useUIStore();
  const [isEnabled, setIsEnabled] = useState(isBiometricEnabled);

  useEffect(() => {
    loadBiometricSetting();
  }, []);

  const loadBiometricSetting = async () => {
    try {
      const stored = await AsyncStorage.getItem(BIOMETRIC_STORAGE_KEY);
      if (stored !== null) {
        setIsEnabled(stored === 'true');
      }
    } catch (error) {
      console.error('Failed to load biometric setting:', error);
    }
  };

  const handleToggle = async (value: boolean) => {
    if (value) {
      // Show alert explaining what biometric auth does
      Alert.alert(
        'Bật xác thực sinh trắc học',
        'Bạn có thể sử dụng vân tay hoặc Face ID để đăng nhập nhanh hơn vào lần sau. Tính năng này chỉ hoạt động trên thiết bị của bạn.',
        [
          {
            text: 'Hủy',
            style: 'cancel',
          },
          {
            text: 'Bật',
            onPress: async () => {
              try {
                await AsyncStorage.setItem(BIOMETRIC_STORAGE_KEY, 'true');
                setIsEnabled(true);
                toggleBiometric();
              } catch (error) {
                Alert.alert('Lỗi', 'Không thể bật xác thực sinh trắc học');
              }
            },
          },
        ]
      );
    } else {
      Alert.alert(
        'Tắt xác thực sinh trắc học',
        'Bạn sẽ cần nhập mật khẩu để đăng nhập vào lần sau.',
        [
          {
            text: 'Hủy',
            style: 'cancel',
          },
          {
            text: 'Tắt',
            onPress: async () => {
              try {
                await AsyncStorage.setItem(BIOMETRIC_STORAGE_KEY, 'false');
                setIsEnabled(false);
                toggleBiometric();
              } catch (error) {
                Alert.alert('Lỗi', 'Không thể tắt xác thực sinh trắc học');
              }
            },
          },
        ]
      );
    }
  };

  const biometricType = Platform.OS === 'ios' ? 'Face ID / Touch ID' : 'Vân tay';

  const features = [
    {
      icon: <CheckIcon />,
      title: 'Đăng nhập nhanh',
      description: 'Không cần nhập mật khẩu mỗi lần đăng nhập',
    },
    {
      icon: <CheckIcon />,
      title: 'Bảo mật cao',
      description: 'Sử dụng sinh trắc học độc nhất của bạn',
    },
    {
      icon: <CheckIcon />,
      title: 'Tiện lợi',
      description: 'Chỉ cần một chạm để truy cập ứng dụng',
    },
  ];

  return (
    <View style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity onPress={() => navigation?.goBack()} style={styles.backButton}>
          <BackIcon />
        </TouchableOpacity>
        <Text style={styles.headerTitle}>
          Xác thực sinh trắc học
        </Text>
      </View>

      <ScrollView
        style={styles.scrollView}
        showsVerticalScrollIndicator={false}
      >
        {/* Icon */}
        <View style={styles.iconSection}>
          <View style={[styles.iconContainer, { backgroundColor: `${colors.primary}15` }]}>
            {Platform.OS === 'ios' ? (
              <FaceIdIcon size={48} />
            ) : (
              <FingerprintIcon color={colors.primary} size={48} />
            )}
          </View>
          <Text style={styles.iconTitle}>
            {biometricType}
          </Text>
          <Text style={styles.iconSubtitle}>
            Bảo mật tài khoản bằng sinh trắc học
          </Text>
        </View>

        {/* Toggle Card */}
        <View style={styles.toggleCard}>
          <View style={styles.toggleContent}>
            <View style={styles.toggleTextContainer}>
              <Text style={styles.toggleTitle}>
                Sử dụng {biometricType}
              </Text>
              <Text style={styles.toggleDescription}>
                Bật để đăng nhập nhanh hơn
              </Text>
            </View>
            <View style={styles.toggleControl}>
              {isEnabled ? <UnlockIcon /> : <LockIcon />}
              <Switch
                value={isEnabled}
                onValueChange={handleToggle}
                trackColor={{ false: '#E5E7EB', true: colors.primary }}
                thumbColor={isEnabled ? 'white' : '#F3F4F6'}
                ios_backgroundColor="#E5E7EB"
              />
            </View>
          </View>
        </View>

        {/* Features */}
        <View style={styles.featuresCard}>
          <Text style={styles.featuresTitle}>
            Lợi ích
          </Text>
          {features.map((feature, index) => (
            <View
              key={index}
              style={[styles.featureItem, index !== features.length - 1 ? styles.featureItemBorder : {}]}
            >
              <View style={styles.featureIconContainer}>
                <View style={styles.featureIcon}>
                  {feature.icon}
                </View>
              </View>
              <View style={styles.featureTextContainer}>
                <Text style={styles.featureTitle}>
                  {feature.title}
                </Text>
                <Text style={styles.featureDescription}>
                  {feature.description}
                </Text>
              </View>
            </View>
          ))}
        </View>

        {/* Security Note */}
        <View style={[styles.securityNote, { backgroundColor: '#FFFBEB' }]}>
          <Text style={styles.securityNoteTitle}>
            🔐 Lưu ý bảo mật
          </Text>
          <Text style={styles.securityNoteText}>
            Dữ liệu sinh trắc học được lưu trữ an toàn trên thiết bị của bạn và không bao giờ được gửi đến máy chủ. Tuy nhiên, bạn vẫn cần nhớ mật khẩu để khôi phục tài khoản khi cần.
          </Text>
        </View>
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F9FAFB',
  },
  header: {
