export const ProfileScreen: React.FC<ProfileScreenProps> = ({ navigation }) => {
  const { user, logout } = useAuthStore();
  const { isBiometricEnabled } = useUIStore();

  const getInitials = (name?: string) => {
    if (!name) return 'U';
    const parts = name.split(' ').filter(p => p.length > 0);
    if (parts.length === 0) return 'U';
    if (parts.length === 1) return (parts[0] || '').slice(0, 2).toUpperCase();
    const first = (parts[0] || '').charAt(0);
    const last = (parts[parts.length - 1] || '').charAt(0);
    return `${first}${last}`.toUpperCase();
  };

  const getIcon = (iconName: string, color: string) => {
    switch (iconName) {
      case 'user': return <UserIcon color={color} />;
      case 'lock': return <LockIcon color={color} />;
      case 'fingerprint': return <FingerprintIcon color={color} />;
      case 'help': return <HelpIcon color={color} />;
      case 'support': return <SupportIcon color={color} />;
      default: return <UserIcon color={color} />;
    }
  };

  const handleLogout = () => {
    Alert.alert(
      'Đăng xuất',
      'Bạn có chắc chắn muốn đăng xuất?',
      [
        {
          text: 'Hủy',
          style: 'cancel',
        },
        {
          text: 'Đăng xuất',
          style: 'destructive',
          onPress: async () => {
            try {
              await logout();
            } catch (error) {
              Alert.alert('Lỗi', 'Không thể đăng xuất. Vui lòng thử lại.');
            }
          },
        },
      ]
    );
  };

  const getRoleLabel = (role?: string) => {
    switch (role) {
      case 'parent': return 'Phụ huynh';
      case 'student': return 'Học sinh';
      case 'teacher': return 'Giáo viên';
      case 'admin': return 'Quản trị viên';
      default: return role || 'Người dùng';
    }
  };

  return (
    <View style={styles.container}>
      {/* Header */}
      <View style={[styles.header, { backgroundColor: colors.primary }]}>
        <Text style={styles.headerLabel}>
          Cá nhân
        </Text>
        <Text style={styles.headerTitle}>
          {user?.name || 'Người dùng'}
        </Text>
        <Text style={styles.headerEmail}>
          {user?.email}
        </Text>
        <View style={styles.roleBadgeContainer}>
          <View style={styles.roleBadge}>
            <Text style={styles.roleBadgeText}>
              {getRoleLabel(user?.role)}
            </Text>
          </View>
        </View>
      </View>

      {/* Content */}
      <ScrollView
        style={styles.scrollView}
        showsVerticalScrollIndicator={false}
      >
        {/* Profile Card */}
        <View style={styles.profileCard}>
          <View style={styles.profileCardInner}>
            <View style={[styles.avatar, { backgroundColor: `${colors.primary}20` }]}>
              <Text style={[styles.avatarText, { color: colors.primary }]}>
                {getInitials(user?.name)}
              </Text>
            </View>
            <View style={styles.profileInfo}>
              <Text style={styles.profileName}>
                {user?.name}
              </Text>
              <Text style={styles.profileRole}>
                {getRoleLabel(user?.role)}
              </Text>
            </View>
          </View>
        </View>

        {/* Menu Items */}
        <View style={styles.menuContainer}>
          {MENU_ITEMS.map((item, index) => (
            <TouchableOpacity
              key={item.id}
              style={[styles.menuItem, index !== MENU_ITEMS.length - 1 ? styles.menuItemBorder : {}]}
              onPress={() => navigation?.navigate(item.route as never)}
              activeOpacity={0.7}
            >
              <View style={[styles.menuIcon, { backgroundColor: `${item.color}15` }]}>
                {getIcon(item.icon, item.color)}
              </View>
              <View style={styles.menuTextContainer}>
                <Text style={styles.menuTitle}>
                  {item.title}
                </Text>
                <Text style={styles.menuDescription}>
                  {item.description}
                </Text>
              </View>
              <RightArrowIcon />
            </TouchableOpacity>
          ))}
        </View>

        {/* Logout Button */}
        <TouchableOpacity
          style={styles.logoutButton}
          onPress={handleLogout}
          activeOpacity={0.7}
        >
          <LogoutIcon />
          <Text style={styles.logoutText}>
            Đăng xuất
          </Text>
        </TouchableOpacity>

        {/* Version Info */}
        <View style={styles.versionContainer}>
          <Text style={styles.versionText}>
            Phiên bản 1.0.0
          </Text>
        </View>
      </ScrollView>
    </View>
  );
};

