const SUPPORT_INFO = {
  phone: '1900 xxxx',
  email: 'support@econtact.vn',
  address: 'Địa chỉ: [Địa chỉ trường học]',
  workingHours: 'Thứ 2 - Thứ 6: 7:30 - 17:00\nThứ 7: 7:30 - 11:30',
};

interface ContactForm {
  name: string;
  email: string;
  subject: string;
  message: string;
}

export const SupportScreen: React.FC<SupportScreenProps> = ({ navigation }) => {
  const { user } = useAuthStore();
  const { showNotification } = useUIStore();
  const [isLoading, setIsLoading] = useState(false);
  const [formData, setFormData] = useState<ContactForm>({
    name: user?.name || '',
    email: user?.email || '',
    subject: '',
    message: '',
  });

  const handleCall = () => {
    Linking.openURL(`tel:${SUPPORT_INFO.phone}`).catch(() => {
      Alert.alert('Lỗi', 'Không thể thực hiện cuộc gọi');
    });
  };

  const handleEmail = () => {
    Linking.openURL(`mailto:${SUPPORT_INFO.email}`).catch(() => {
      Alert.alert('Lỗi', 'Không thể mở ứng dụng email');
    });
  };

  const handleSubmit = async () => {
    if (!formData.subject.trim()) {
      Alert.alert('Lỗi', 'Vui lòng nhập chủ đề');
      return;
    }

    if (!formData.message.trim()) {
      Alert.alert('Lỗi', 'Vui lòng nhập nội dung tin nhắn');
      return;
    }

    setIsLoading(true);

    try {
      // Store support request in database (you'd need to create a support_requests table)
      const { error } = await supabase
        .from('support_requests')
        .insert({
          user_id: user?.id,
          name: formData.name,
          email: formData.email,
          subject: formData.subject,
          message: formData.message,
          status: 'pending',
          created_at: new Date().toISOString(),
        });

      if (error) {
        // If table doesn't exist yet, just show success message
        console.log('Support request:', formData);
      }

      showNotification({
        type: 'success',
        message: 'Gửi yêu cầu hỗ trợ thành công! Chúng tôi sẽ liên hệ sớm.',
      });

      // Clear form
      setFormData({
        ...formData,
        subject: '',
        message: '',
      });
    } catch (error) {
      console.error('Submit support error:', error);
      Alert.alert('Lỗi', 'Không thể gửi yêu cầu. Vui lòng thử lại.');
    } finally {
      setIsLoading(false);
    }
  };

  const ContactOption: React.FC<{
    icon: React.ReactNode;
    title: string;
    value: string;
    onPress: () => void;
  }> = ({ icon, title, value, onPress }) => (
    <TouchableOpacity
      style={styles.contactOption}
      onPress={onPress}
      activeOpacity={0.7}
    >
      <View style={[styles.contactIcon, { backgroundColor: `${colors.primary}15` }]}>
        {icon}
      </View>
      <View style={styles.contactInfo}>
        <Text style={styles.contactTitle}>
          {title}
        </Text>
        <Text style={styles.contactValue}>
          {value}
        </Text>
      </View>
      <RightArrowIcon />
    </TouchableOpacity>
  );

  return (
    <View style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity onPress={() => navigation?.goBack()} style={styles.backButton}>
          <BackIcon />
        </TouchableOpacity>
        <Text style={styles.headerTitle}>
          Hỗ trợ
        </Text>
      </View>

      <ScrollView
        style={styles.scrollView}
        showsVerticalScrollIndicator={false}
      >
        {/* Contact Options */}
        <Text style={styles.sectionTitle}>
          Liên hệ trực tiếp
        </Text>

        <View style={styles.contactOptionsContainer}>
          <ContactOption
            icon={<PhoneIcon />}
            title="Điện thoại"
            value={SUPPORT_INFO.phone}
            onPress={handleCall}
          />
          <ContactOption
            icon={<EmailIcon />}
            title="Email"
            value={SUPPORT_INFO.email}
            onPress={handleEmail}
          />
        </View>

        {/* Info Card */}
        <View style={styles.infoCard}>
          <Text style={styles.infoCardTitle}>
            Thông tin hỗ trợ
          </Text>
          <View style={styles.infoRow}>
            <Text style={styles.infoLabel}>Địa chỉ:</Text>
            <Text style={styles.infoValue}>
              {SUPPORT_INFO.address}
            </Text>
          </View>
          <View style={styles.infoRow}>
            <Text style={styles.infoLabel}>Giờ làm việc:</Text>
            <Text style={styles.infoValue}>
              {SUPPORT_INFO.workingHours}
            </Text>
          </View>
        </View>

        {/* Contact Form */}
        <Text style={styles.sectionTitle}>
          Gửi yêu cầu hỗ trợ
        </Text>

        <View style={styles.formCard}>
          {/* Name */}
          <View style={styles.inputContainer}>
            <Text style={styles.inputLabel}>
              Họ và tên
            </Text>
            <View style={styles.inputWrapper}>
              <TextInput
                value={formData.name}
                onChangeText={(text) => setFormData({ ...formData, name: text })}
                placeholder="Nhập họ và tên"
                style={styles.input}
                placeholderTextColor="#9CA3AF"
              />
            </View>
          </View>

          {/* Email */}
          <View style={styles.inputContainer}>
            <Text style={styles.inputLabel}>
              Email
            </Text>
            <View style={styles.inputWrapper}>
              <TextInput
                value={formData.email}
                onChangeText={(text) => setFormData({ ...formData, email: text })}
                placeholder="Nhập email"
                keyboardType="email-address"
                style={styles.input}
                placeholderTextColor="#9CA3AF"
              />
            </View>
          </View>

          {/* Subject */}
          <View style={styles.inputContainer}>
            <Text style={styles.inputLabel}>
              Chủ đề
            </Text>
            <View style={styles.inputWrapper}>
              <TextInput
                value={formData.subject}
                onChangeText={(text) => setFormData({ ...formData, subject: text })}
                placeholder="Nhập chủ đề"
                style={styles.input}
                placeholderTextColor="#9CA3AF"
              />
            </View>
          </View>

          {/* Message */}
          <View style={styles.inputContainer}>
            <Text style={styles.inputLabel}>
              Nội dung
            </Text>
            <View style={[styles.inputWrapper, styles.textAreaWrapper]}>
              <TextInput
                value={formData.message}
                onChangeText={(text) => setFormData({ ...formData, message: text })}
                placeholder="Mô tả vấn đề của bạn..."
                multiline
                textAlignVertical="top"
                style={[styles.input, styles.textAreaInput]}
                placeholderTextColor="#9CA3AF"
              />
            </View>
          </View>

          {/* Submit Button */}
          <TouchableOpacity
            style={[styles.submitButton, { backgroundColor: colors.primary }]}
            onPress={handleSubmit}
            disabled={isLoading}
            activeOpacity={0.8}
          >
            {isLoading ? (
              <ActivityIndicator color="white" />
            ) : (
              <>
                <SendIcon />
                <Text style={styles.submitButtonText}>
                  Gửi yêu cầu
                </Text>
              </>
            )}
          </TouchableOpacity>
        </View>

        {/* Footer */}
        <View style={styles.footer}>
          <Text style={styles.footerText}>
            Chúng tôi sẽ phản hồi trong vòng 24-48 giờ
          </Text>
        </View>
      </ScrollView>
    </View>
  );
};

